BEGIN TRANSACTION;

DROP TABLE IF EXISTS users;
DROP TABLE IF EXISTS responses;
DROP TABLE IF EXISTS messageLog;
DROP TABLE IF EXISTS categories;
DROP TABLE IF EXISTS interview_questions;

CREATE TABLE users (
	user_id SERIAL,
	username varchar(50),
	current_category varchar(20),
	current_topic varchar(40),
	current_keyword varchar(30),
	CONSTRAINT PK_user PRIMARY KEY (user_id)
);

CREATE TABLE responses (
    response_id SERIAL,
    category varChar(20),
    topic_display varChar(30),
    topic_search varChar(30),
    keyword_display varChar(40),
    keyword_search varChar(40),
    subkeyword_display varChar(30),
    subkeyword_search varChar(30),
    content_type varChar(20),
    display varChar(600),
    display_type varChar(10),
    Link varChar(110),
    date_last_updated varChar(10),
    codee_style varChar(10)
);

CREATE TABLE categories (
	category_id SERIAL,
	category_name varchar(50)
);

CREATE TABLE messageLog (
        message_id SERIAL,
        user_id int,
        sender varChar(10),
        display varChar(830),
        response_id int,
        question_id int
);

CREATE TABLE interview_questions (
    question_id SERIAL,
    display varChar(830),
    answer varChar(10),
    link varChar(110),
    display_type varChar(10),
    content_type varChar(10),
    codee_style varChar(10)
);




COMMIT TRANSACTION;
